mapper.select:
select id, articlescrap_id, ip, judge_level, judge_message, uname, email, doubt_level, feedback_type, create_time from feedback
and articlescrap_id=#{articlescrap_id} and feedback_type = #{feedback_type} and create_time >= #{timeFrom} and create_time <= #{timeTo}
order by create_time desc limit #{beginNum},#{onePageSize}

	    select count(1)
	    from feedback 

         and articlescrap_id=#{articlescrap_id}
           and feedback_type = #{feedback_type} 
           and create_time >= #{timeFrom} 
           and create_time <= #{timeTo}
